CREATE TABLE `dc_art_block` (
  `block_id` int(11) NOT NULL COMMENT 'ID',
  `classid` int(11) DEFAULT '0',
  `title` varchar(500) DEFAULT NULL COMMENT '标题',
  `marker` varchar(255) DEFAULT NULL COMMENT '标识符号',
  `content` text COMMENT '内容',
  `updatetime` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `block_remarks` text COMMENT '备注',
  `is_enabled` tinyint(1) DEFAULT '1' COMMENT '是否启用，1=启用'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='碎片';

